mapper.select:
select count(*) from products

	where name like #{keyword}


	where contents like #{keyword}

select * from products

	where name like #{keyword}


	where contents like #{keyword}

order by num desc

mapper.update:
update products set 
name=#{name},	company=#{company},	price=#{price},	stock=#{stock},	point=#{point},	contents=#{contents},image=#{image}
where num=#{num}

update products set
stock = stock - #{stock}
where num = #{num}
